script:
frappe.ui.form.on('account doc', {
    refresh(frm) {
        // Add a label highlight effect
        frm.fields_dict.client_name.$wrapper.css({
            'background-color': '#e0ffe0',
            'font-weight': 'bold'
        });

        // Automatically set a field if empty
        if (!frm.doc.client_name) {
            frm.set_value('client_name', 'Auto-filled by script');
            frappe.show_alert({ 
                message: __('Client Name auto-filled'), 
                indicator: 'green' 
            });
        }
    }
});

frappe.ui.form.on('kava doc', {
    refresh(frm) {
        frm.add_custom_button('Say Hello', () => {
            frappe.msgprint(__('Hello from Galaxynext App!'));
        });
    }
});

frappe.ui.form.on('disha doc', {
    refresh(frm) {
        frm.add_custom_button('Mark as Verified', () => {
            frm.set_value('status', 'Verified');
            frappe.show_alert({
                message: __('Document marked as Verified'),
                indicator: 'green'
            });
        });
    }
});

frappe.ui.form.on('Project', {
    refresh(frm) {
        // Add a label highlight effect
        frm.fields_dict.client_name.$wrapper.css({
            'background-color': '#e0ffe0',
            'font-weight': 'bold'
        });

        // Automatically set a field if empty
        if (!frm.doc.client_name) {
            frm.set_value('client_name', 'Auto-filled by script');
            frappe.show_alert({ 
                message: __('Client Name auto-filled'), 
                indicator: 'green' 
            });
        }
    }
});

frappe.ui.form.on('Issue', {
    refresh(frm) {
        // --- Effect 1: Auto-fill client_title if empty and highlight ---
        if (!frm.doc.client_title) {
            frm.set_value('client_title', 'Auto-filled by script');
            frappe.show_alert({ 
                message: __('Client Title auto-filled'), 
                indicator: 'green' 
            });
        }
        frm.fields_dict.client_title.$wrapper.css({
            'background-color': '#fff3e0',
            'font-weight': 'bold'
        });

        // --- Effect 2: Add custom button to mark document_state ---
        frm.add_custom_button('Approve Document', () => {
            frm.set_value('document_state', 'Approved');
            frappe.show_alert({
                message: __('Document marked as Approved'),
                indicator: 'green'
            });
        });
    }
});

 frappe.ui.form.on('Customer', {
    test_value_1: function(frm) {
        // જયારે Test Value 1 change થાય
        frappe.show_alert({
            message: __('You typed: {0}', [frm.doc.test_value_1]),
            indicator: 'blue'
        }, 5);
    },

    test_value_2: function(frm) {
        // Test Value 2 change થાય ત્યારે Test Value 1 auto update
        if (frm.doc.test_value_2) {
            frm.set_value('test_value_1', frm.doc.test_value_2.toUpperCase());
            frappe.msgprint(__('Test Value 1 auto-filled with UPPERCASE of Test Value 2'));
        }
    }
});
