script:
frappe.ui.form.on('kava doc', {
    refresh(frm) {
        frm.add_custom_button('Say Hello', () => {
            frappe.msgprint(__('Hello from Galaxynext App!'));
        });
    }
});

frappe.ui.form.on('disha doc', {
    refresh(frm) {
        frm.add_custom_button('Mark as Verified', () => {
            frm.set_value('status', 'Verified');
            frappe.show_alert({
                message: __('Document marked as Verified'),
                indicator: 'green'
            });
        });
    }
});
