frappe.ui.form.on("Opportunity", {
    refresh(frm) {
        // Page refresh થતાની સાથે message બતાવશે
        frappe.msgprint("Contact form loaded successfully!");
    },

    test_checkbox(frm) {
        if (frm.doc.test_checkbox) {
            frappe.msgprint("✅ Checkbox selected!");
        } else {
            frappe.msgprint("❌ Checkbox unselected!");
        }
    },

    test_remarks(frm) {
        if (frm.doc.test_remarks) {
            frappe.show_alert({
                message: __("You entered: " + frm.doc.test_remarks),
                indicator: "green"
            }, 5);
        }
    }
});

