script:
 frappe.ui.form.on('ERPNext Doc', {  // Replace with your Doctype
    // Trigger when the form loads
    onload: function(frm) {
        // If title is empty, set default value
        if (!frm.doc.title) {
            frm.set_value('title', 'Default Title');
        }

        // Show message with current title
        frappe.msgprint(`Current Title: ${frm.doc.title}`);
        console.log("Form Loaded:", frm.doc);
    },

    // Trigger when the 'title' field changes
    title: function(frm) {
        console.log("Title changed to:", frm.doc.title);
        frappe.msgprint(`You updated the Title to: ${frm.doc.title}`);
    },

    // Trigger before saving the form
    before_save: function(frm) {
        // Ensure title is not empty
        if (!frm.doc.title) {
            frm.set_value('title', 'Default Title');
            frappe.msgprint("Title was empty, default value applied!");
        }
    }
});
