script:
frappe.ui.form.on('Design Import', {
    refresh: function(frm) {
        // By default hide both tables
        frm.toggle_display("original_design_portion", 0);
        frm.toggle_display("production_design_portion", 0);

        // Handle tab click
        setTimeout(() => {
            // Original Tab click event
            $('[data-fieldname="original_tab"]').on("click", function() {
                frm.toggle_display("original_design_portion", 1);
                frm.toggle_display("production_design_portion", 0);
            });

            // Production Tab click event
            $('[data-fieldname="production_tab"]').on("click", function() {
                frm.toggle_display("original_design_portion", 0);
                frm.toggle_display("production_design_portion", 1);
            });
        }, 500);
    }
});
